package AutomationClass3;

public class LoopCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c = 20;
		if(a>b)
		{
			System.out.println("A > B");
		}
		if(a>=b)
		{
			System.out.println("A > = B");
		}
		if(a<b)
		{
			System.out.println("A < B");
		}
		if(b==c)
		{
			System.out.println("B = C");
		}
		if(a!=b)
		{
			System.out.println("A != B");
		}
	}

}
